import OpenAI from 'openai';
const client = new OpenAI({
    apiKey: process.env.OPENAI_API_KEY,
    baseURL: process.env.OPENAI_API_BASE_URL || "https://api.openai.com/v1",
    timeout: 120000, // 10 seconds
    maxRetries: 3, // Retry up to 3 times on failure
});
await client.responses.create({
    model: 'gpt-4.1',
    instructions: 'You are a coding assistant that talks like a pirate',
    input: 'veridenify.io için omnpage raporunu hazırla.', tools: [
        {
            "type": "mcp",
            "server_label": "dataforseo-mcp-server",
            "server_url": "https://mcp.dataforseo.com/mcp",
            "require_approval": "never",
            "headers": {
                "Authorization": `Basic ${Buffer.from(`${process.env.DATAFORSEO_USERNAME}:${process.env.DATAFORSEO_PASSWORD}`).toString('base64')}`
            }
        }
    ]
}).then(response => {
    console.log("Response:", response);
}).catch(error => {
    console.error("Error:", error);
});
